import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./MainContent.css";

const MainContent = ({notes,onDeleteNote,activeNote}) => {
  const [body, setBody] = useState("");
  const handleBody = (e) => {
    console.log(e);
    setBody(e);
  };
  const state = {
    title: "",
    content: "",
  };

  const save =(e)=>{
    console.log("save");
    //title={title};
    //content={body};

    return 0;}
  //this.props.addNoteHandler(this.state);
  //this.setState({title:"",content:""});
  //value={this.state.title}
  //onChange={(e)=>this.setState({title: e.target.value})}

  //}

  return (
    <div className="MainContent">
      <div className="MCTitle">
        <div className="header_title">
          <input type="text" id="title" value={activeNote.title} autoFocus className="TitleInput" />
        </div>
        <div className="MCTitle2">
        <button className="Save" onClick={save}>Save</button>
          {notes.map((note) => (

            <button className="Delete" onClick={()=>onDeleteNote(note.id)} >Delete</button>


        ))}

        </div>
      </div>
      <ReactQuill
        placeholder="Start writing here..."
        modules={MainContent.modules}
        formats={MainContent.formats}
        onChange={handleBody}
        //onChange={(e)=>this.setState({content: e.target.value})}
        //value={this.state.content}
        value={activeNote.body}
      />
    </div>

  );
};
MainContent.modules = {
  toolbar: [
    [{ size: [] }],
    ["bold", "italic", "underline"],
    ["link"],
    [{ list: "ordered" }, { list: "bullet" }],
    ["clean"],
  ],
};
MainContent.formats = [
  "size",
  "bold",
  "italic",
  "underline",
  "link",
  "list",
  "code-block",
];
const options = {
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "numeric",
  minute: "numeric",
};

const formatDate = (when) => {
  const formatted = new Date(when).toLocaleString("en-US", options);
  if (formatted === "Invalid Date") {
    return "";
  }
  return formatted;
};

export default MainContent;
