import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./MainContent.css";

const MainContent = ({notes,onDeleteNote,activeNote, onUpdateNote}) => {
  const onEditField=(key,value)=>{
    onUpdateNote({
      ...activeNote,
      [key]:value,
      lastModified:Date.now(),
    });
  };
  if(!activeNote){
    return <div className="no-active-note">Select a note or create a new note</div>
  };

  return (
    <div className="MainContent">
      <div className="MCTitle">
        <div className="header_title">
          <input type="text" id="title" value={activeNote.title} onChange={(e)=>onEditField("title",e.target.value)} autoFocus />
        </div>
        <div className="MCTitle2">
        <button className="Save" >Save</button>
          {notes.map((note) => (

            <button className="Delete" onClick={()=>onDeleteNote(note.id)} >Delete</button>


        ))}

        </div>
      </div>
      <ReactQuill
        placeholder="Start writing here..."
        modules={MainContent.modules}
        formats={MainContent.formats}
        value={activeNote.body}
        onChange={(e)=>onEditField("body",e.target.value)} autoFocus
        //onChange={(e)=>this.setState({content: e.target.value})}
        //value={this.state.content}

      />
    </div>

  );
};
MainContent.modules = {
  toolbar: [
    [{ size: [] }],
    ["bold", "italic", "underline"],
    ["link"],
    [{ list: "ordered" }, { list: "bullet" }],
    ["clean"],
  ],
};
MainContent.formats = [
  "size",
  "bold",
  "italic",
  "underline",
  "link",
  "list",
  "code-block",
];
const options = {
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "numeric",
  minute: "numeric",
};

const formatDate = (when) => {
  const formatted = new Date(when).toLocaleString("en-US", options);
  if (formatted === "Invalid Date") {
    return "";
  }
  return formatted;
};

export default MainContent;
