import React, {useState,Component}from "react";
import "./App.css";
import MainContent from "./MainContent.js";
import SideBar from "./SideBar.js";
import uuid from "react-uuid";
function App(){

  //const[state, setState] =useState({
  //})

  //toggleShowMe(){
    //this.setState({showMe: !this.state.showMe})
//};
  const [notes, setNotes] = useState([]);
  const[activeNote,setActiveNote] = useState(false);
  const onAddNote = () => {
    const newNote = {
      id:uuid(),
      title: "Untitled",
      body:"",
      lastModified: Date.now(),
    }
    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
  }
  const onDeleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  }
  const onUpdateNote = (updatedNote) => {
    const updatedNotesArray = notes.map((note) => {
      if(note.id === updatedNote.id){
        return updatedNote;
      }
      return note;

    });
    setNotes(updatedNotesArray);
  }
  const getActiveNote = () => {return notes.find(({id}) => id === activeNote)};
  return (
    <div className="ui container">
      <div className='header'>
            <div className='functionBar'><button className="btn" onClick={()=>this.toggleShowMe()}>&#9776;</button></div>
            <div className='title'>
                <h1 id="title1">Lotionmotion hello</h1>
                <p id="title2">Like Notion, but worse</p>
            </div>
        </div>
    <div className="container2">

    {/*this.state.showMe?
    <div className="SideBar">
    <div className="SideBarMenu">
      <SideBar notes ={notes} onAddNote={onAddNote} activeNote={activeNote} setActiveNote={setActiveNote}/>
    </div>
</div>
:null
  */}
  <div className="SideBar">
    <div className="SideBarMenu">
      <SideBar notes ={notes} onAddNote={onAddNote} activeNote={activeNote} setActiveNote={setActiveNote}/>
    </div>
</div>









        <div className="MainContent">
            <MainContent notes ={notes} onDeleteNote={onDeleteNote} activeNote={getActiveNote()} onUpdateNote={onUpdateNote}/>
        </div>
      </div>

    </div>

  );
}

export default App;
