import React,{useState} from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./MainContent.css";

class MainContent extends React.componet {
    //const[body, setBody] = useState("");
    //const handleBody = (e) => {
       // console.log(e);
        //setBody(e);
    state={
        title:"",
        content:"",
    }

    save =(e)=>{
        this.props.addNoteHandler(this.state);
        this.setState({title:"",content:""});

    }
    render(){
        return(
            <div className="MainContent">
                <div className="MCTitle">
                <div className="header_title">
                <input type="text" placeholder="Title" className="TitleInput" value={this.state.title} onChange={(e)=>this.setState({title: e.target.value})}/>
                </div>
                <div className="MCTitle2">
                    <button className="Save">Save</button>
                    <button className="Delete">Delete</button>
                </div>
                </div>

            <div className="date">{formatDate}</div>
            <ReactQuill placeholder="Start writing here..."
            modules={MainContent.modules}
            formats={MainContent.formats}
            onChange={(e)=>this.setState({content: e.target.value})}
            value={this.state.content}
            />

            </div>)}

    ;


};
MainContent.modules = {
    toolbar: [
        [{size:[]}],
        ["bold","italic","underline"],
        ["link"],
        [{list:"ordered"},{list:"bullet"}],
        ["clean"],
    ],
};
MainContent.formats = [
    "size",
    "bold",
    "italic",
    "underline",
    "link",
    "list",
    "code-block",
];
const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
};

const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
        return "";
    }
    return formatted;
};
export default MainContent;
