import React from "react";
import './SideBar.css';

const SideBar = ({notes,onAddNote, activeNote, setActiveNote}) => {

    const sorted = notes.sort((a,b)=>b.lastModified-a.lastModified);

    return(
    <div className="Bar">
            <div className="SideBarTitle">
                <h3 className="Notes"><b>Notes</b></h3>
                <button className="PlusButton" onClick={onAddNote}>&#10133;</button>
            </div>
            <div className="SideBarNotes">
                {sorted.map(({id,title,body,lastModified},i) => (
                    <div className={`SideBarNote ${id === activeNote && "active"}`} onClick={()=>setActiveNote(id)}>
                    <div className="SideBarNoteTile">
                    <strong>{title}</strong>
                    </div>

                <small className="note-meta">
                {new Date(lastModified).toLocaleDateString("en-US",{month:"short",day:"2-digit",year:"numeric",hour:"2-digit",minute:"2-digit"})}
                </small>
                <p>{body && body.substr(0, 100) + "..."}</p>
            </div>

        ))}
            </div>
        </div>
        );


};

export default SideBar;
