import React from "react";
import Note from "./Note";
import './SideBar.css';

const SideBar = ({notes,onAddNote, activeNote, setActiveNote}) => {



    return(
    <div className="Bar">
            <div className="SideBarTitle">
                <h3 className="Notes"><b>Notes</b></h3>
                <button className="PlusButton" onClick={onAddNote}>&#10133;</button>
            </div>
            <div className="SideBarNotes">
                {notes.map((note) => (
                    <div className={`SideBarNote ${note.id === activeNote && "active"}`} onClick={()=>setActiveNote(note.id)}>
                        <div className="SideBarNoteTile">
                        <strong>{note.title}</strong>
                        </div>
                    <p>{note.body && note.body.substr(0,1000)+"..."}</p>
                    <small className="note-meta">
                    {new Date(note.lastModified).toLocaleDateString("en-US",{month:"short",day:"2-digit",year:"numeric",hour:"2-digit",minute:"2-digit"})}
                </small>
                <p>Note Preview</p>
                </div>

                ))}

            </div>
            </div>);

};

export default SideBar;
