import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./MainContent.css";
import {useNavigate} from "../hooks/useNavigate";

const Read = ({notes,onDeleteNote,activeNote, onUpdateNote}) => {
  const[content,setContent] = useState("");
  const onEditField=(key,value)=>{
    onUpdateNote({
      ...activeNote,
      [key]:value,
      lastModified:Date.now(),
    });
  };
  if(!activeNote){
    return <div className="no-active-note">Select a note or create a new note</div>
  };

  return (
    <div className="MainContent">
      <div className="MCTitle">
        <div className="header_title">
          <div>{activeNote.title}</div>
        </div>
        <div className="MCTitle2">
        <EditButton/>
        {notes.map((note) => (<button className="Delete" onClick={()=>onDeleteNote(note.id)} >Delete</button>))}

        </div>
      </div>
      <div>{activeNote.body}</div>
    </div>

  );
};
Read.modules = {
  toolbar: [
    [{ size: [] }],
    ["bold", "italic", "underline"],
    ["link"],
    [{ list: "ordered" }, { list: "bullet" }],
    ["clean"],
  ],
};
Read.formats = [
  "size",
  "bold",
  "italic",
  "underline",
  "link",
  "list",
  "code-block",
];
const options = {
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "numeric",
  minute: "numeric",
};
export default Read;
function EditButton(){
  const navigate=useNavigate();

  const Edit=()=>{
       navigate("/edit");
    }
    return(
        <button className="Edit" onClick={onEdit}>Edit</button>
    );

}
